        // Helper method to define consistent buttons
        private void CreateSidebarButton(System.Windows.Forms.Button btn, string text, int top, System.Drawing.Font font, System.Drawing.Color color, bool active = false)
        {
            btn = new System.Windows.Forms.Button();
            btn.Text = text;
            btn.Font = font;
            btn.ForeColor = color;
            btn.TextAlign = System.Drawing.ContentAlignment.MiddleLeft;
            btn.FlatStyle = System.Windows.Forms.FlatStyle.Flat;
            btn.FlatAppearance.BorderSize = 0;
            btn.BackColor = active ? System.Drawing.Color.FromArgb(230, 240, 255) : System.Drawing.Color.White;
            btn.Size = new System.Drawing.Size(220, 36);
            btn.Location = new System.Drawing.Point(0, top);
            btn.Padding = new System.Windows.Forms.Padding(20, 0, 0, 0);
            btn.Cursor = System.Windows.Forms.Cursors.Hand;
            this.panelSidebar.Controls.Add(btn);
        }

